from typing import Tuple
from rpcf.rpcbase import *
from rpcf.proxy import *
from seribase import CSerialBase
from HelloWorldstructs import *
import errno

from HelloWorldSvccli import CHelloWorldSvcProxy
import os
import time

import signal
bExit = False
def SigHandler( signum, frame ):
    global bExit
    bExit = True


def MainEntryCli() :
    ret = 0
    signal.signal( signal.SIGINT, SigHandler)
    oContext = PyRpcContext( 'PyRpcProxy' )
    with oContext as ctx:
        if ctx.status < 0:
            ret = ctx.status
            print( os.getpid(), 
                "Error start PyRpcContext %d" % ret )
            return ret
        
        print( "start to work here..." )
        strPath_ = os.path.dirname( os.path.realpath( __file__) )
        strPath_ += '/HelloWorlddesc.json'
        oProxy_HelloWorldSvc = CHelloWorldSvcProxy( ctx.pIoMgr,
            strPath_, 'HelloWorldSvc' )
        ret = oProxy_HelloWorldSvc.GetError()
        if ret < 0 :
            return ret
        
        with oProxy_HelloWorldSvc as oProxy:
            global bExit
            try:
                ret = oProxy.GetError()
                if ret < 0 :
                    raise Exception( 'start proxy failed' )
                state = oProxy.oInst.GetState()
                while state == cpp.stateRecovery :
                    time.sleep( 1 )
                    state = oProxy.oInst.GetState()
                    if bExit:
                        break
                if state != cpp.stateConnected or bExit:
                    return ErrorCode.ERROR_STATE
                ret = maincli( oProxy )
            except Exception as err:
                print( err )
            
        oProxy_HelloWorldSvc = None
    oContext = None
    return ret
    
#------customize the method below for your own purpose----
def maincli( 
    oProxy: CHelloWorldSvcProxy ) -> int:
    '''
    adding your code here
    Calling a proxy method like
    'oProxy.Echo( strText )'
    '''
    pret = oProxy.Echo( "Hello, Server" )
    if pret[ 0 ] < 0 :
        print( "Echo failed with error " + str( pret[0] ) )
        return pret[0]
    strResp = pret[1][0]
    print( "Server replys '" + strResp + "'")
    return 0
    
if __name__ == '__main__' :
    ret = MainEntryCli()
    quit( -ret )
